//3의 배수, 5의 배수, 7의 배수
public class 제어문_조건문3_단일조건문4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=15;
		if(num%3==0)
			System.out.println(num+"는(은) 3의 배수");
		if(num%5==0)
			System.out.println(num+"는(은) 5의 배수");
		if(num%7==0)
			System.out.println(num+"는(은) 7의 배수");
	}

}
